mapper.select:
SELECT e.company_id value, e.entName name, IFNULL(e.orgCode,'') orgCode from t_enterpriseinfo e where 1=1

    and e.areaLabel = #{areaLabel}


    and e.entName like CONCAT('%', #{key},'%' )

SELECT DISTINCT t.value, t.remark name, t.subType from t_config t where t.type = #{type}

    and t.subType = #{subType}


    and t.areaLabel = #{areaLabel}

SELECT t.value, t.remark name, GROUP_CONCAT(t.subType) subType from t_config t where t.type = #{type}

    and t.areaLabel = #{areaLabel}

GROUP BY t.value

SELECT '1' flag, COUNT(1) num from t_enterpriseinfo where 1=1
and areaLabel = #{areaLabel}
UNION SELECT '0' flag, value num from t_config where type = 'lostEnt'

SELECT e.industry, e.industryName, COUNT(1) num from t_enterpriseinfo e where 1=1 and e.industryName is not null and e.industryName != ''
and e.areaLabel = #{areaLabel}
GROUP BY e.industry ORDER BY num desc

SELECT 'regulation' `key`, COUNT(1) num, ROUND(COUNT(1)/ (SELECT COUNT(1) from t_enterpriseinfo e where 1=1

    and e.areaLabel = #{areaLabel}
) * 100, 2) rate from t_enterpriseinfo e, t_ent_tag_rel r where e.company_id = r.identifier and r.tag_key =
'regulation'

    and e.areaLabel = #{areaLabel}

UNION ALL
SELECT 'hexin' `key`, COUNT(1) num, ROUND(COUNT(1)/ (SELECT COUNT(1) from t_enterpriseinfo e where 1=1

    and e.areaLabel = #{areaLabel}
) * 100, 2) rate from t_enterpriseinfo e, t_ent_tag_rel r where e.company_id = r.identifier and r.tag_key =
'hexin'

    and e.areaLabel = #{areaLabel}

UNION ALL
SELECT 'gaoduan' `key`, COUNT(1) num, ROUND(COUNT(1)/ (SELECT COUNT(1) from t_enterpriseinfo e where 1=1

    and e.areaLabel = #{areaLabel}
) * 100, 2) rate from t_enterpriseinfo e, t_ent_tag_rel r where e.company_id = r.identifier and r.tag_key =
'gaoduan'

    and e.areaLabel = #{areaLabel}

UNION ALL
SELECT 'gaoxin' `key`, COUNT(1) num, ROUND(COUNT(1)/ (SELECT COUNT(1) from t_enterpriseinfo e where 1=1

    and e.areaLabel = #{areaLabel}
) * 100, 2) rate from t_enterpriseinfo e, t_ent_tag_rel r where e.company_id = r.identifier and r.tag_key =
'gaoxin'

    and e.areaLabel = #{areaLabel}

SELECT ent.orgCode, ent.entName, ent.industry, ent.industryName, ent.company_id companyId, ent.industryTypeName, IFNULL((SELECT SUM(d.value) from t_ent_energyinfo_day d where d.company_id = ent.company_id and d.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 DAY),'%Y-%m-%d') , ' 00:00:00') and d.energyType = 0 GROUP BY d.company_id),0) lastDayValue, IFNULL(ent.built_area,'') builtArea, IFNULL(ent.covered_area,'') coveredArea, IFNULL(ent.micro_park,'') microPark, areaName, areaCode,address, IFNULL((SELECT SUM(d.value) from t_ent_energyinfo_comp_sum d where d.company_id = ent.company_id and d.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m') , '-01 00:00:00') and d.energyType = 0 GROUP BY d.company_id),0) lastMonthValue ,IFNULL((SELECT SUM(d.value) from t_ent_energyinfo_comp_sum d where d.company_id = ent.company_id and d.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m') , '-01 00:00:00') GROUP BY d.company_id),0) lastMonthTCEValue ,IFNULL(ent.unit_ele,0) unitEle ,IFNULL((SELECT d.year_rate from t_ent_energyinfo_comp_sum d where d.company_id = ent.company_id and d.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m') , '-01 00:00:00') and d.energyType = 0),0) yearRate ,IFNULL((SELECT SUM(d.value) from t_ent_energyinfo_comp_sum d where d.company_id = ent.company_id and d.date>= CONCAT(YEAR(NOW()),'-01-01') and d.energyType = 0 GROUP BY d.company_id),0) sumYearValue ,IFNULL((SELECT SUM(d.tce) from t_ent_energyinfo_comp_sum d where d.company_id = ent.company_id and d.date>= CONCAT(YEAR(NOW()),'-01-01') GROUP BY d.company_id),0) sumYearTCEValue ,(SELECT d.production_status from t_ent_energyinfo_comp_sum d where d.company_id = ent.company_id and d.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m') , '-01 00:00:00') and d.energyType = 0) productionStatus ,IFNULL((SELECT ifnull(ROUND(SUM(c.tce) * 100 / s.target_value,2),0) speedValue FROM t_ent_energy_consume s, t_ent_energyinfo_comp_sum c WHERE s.company_id = c.company_id and s.company_id = ent.company_id AND c.date>=STR_TO_DATE(concat(s.date,'-01-01'),'%Y-%m-%d') AND c.date< STR_TO_DATE(concat(s.date+1,'-01-01'),'%Y-%m-%d') and s.date = year(NOW()) GROUP BY c.company_id ),0) energySpeed from t_enterpriseinfo ent
INNER JOIN t_ent_tag_rel tag ON tag.identifier = ent.company_id and tag.tag_key = #{type}
where 1=1
and ent.areaLabel = #{areaLabel}
and ent.areaCode = #{areaCode}
AND CONCAT(SUBSTR(ent.industryType, 1, 3),'00') = CONCAT(SUBSTR(#{industryType}, 1, 3),'00')
GROUP BY ent.company_id ORDER BY sumYearTCEValue DESC,lastDayValue DESC, lastMonthValue DESC

SELECT ear.accnumber consNo
from t_ent_accnumber_rel ear
where ear.type = #{type}  and ear.company_id = #{companyId}

SELECT e.date, TRUNCATE(SUM(e.value), 2) value
from t_ent_energyinfo e where e.company_id = #{companyId} and e.energyType = #{type} and YEAR(e.date) = #{date} and e.accnumber = #{consNo}
GROUP BY e.date ORDER BY e.date

SELECT e.date, TRUNCATE(SUM(e.value), 2) value
from t_ent_energyinfo_day e where e.company_id = #{companyId} and e.energyType = #{type} and DATE_FORMAT(e.date,'%Y-%m') = #{date} and e.accnumber = #{consNo}
GROUP BY e.date ORDER BY e.date

SELECT e.date, TRUNCATE(SUM(e.value), 2) value
from t_ent_energyinfo e where e.company_id = #{companyId} and e.energyType = #{type} and YEAR(e.date) = #{date} - 1 and e.accnumber = #{consNo}
GROUP BY e.date ORDER BY e.date

SELECT e.date, TRUNCATE(SUM(e.value), 2) value
from t_ent_energyinfo_day e where e.company_id = #{companyId} and e.energyType = #{type} and PERIOD_DIFF(date_format(STR_TO_DATE(#{date}, '%Y-%m'),'%Y%m'),date_format(e.date,'%Y%m')) = 12 and e.accnumber = #{consNo}
GROUP BY e.date ORDER BY e.date

SELECT e.date, IF(e.energyType = 2, TRUNCATE(SUM(e.value), 2) , IF(e.energyType > 3, TRUNCATE(SUM(e.tce), 2), TRUNCATE(SUM(e.value), 2)) ) value
from t_ent_energyinfo e where e.company_id = #{companyId} and e.energyType = #{type} and YEAR(e.date) = #{date} and e.accnumber = #{consNo} 
GROUP BY e.date ORDER BY e.date

SELECT e.date, IF(e.energyType = 2, TRUNCATE(SUM(e.value), 2) , IF(e.energyType > 3, TRUNCATE(SUM(e.tce), 2),
TRUNCATE(SUM(e.value), 2)) ) value
from t_ent_energyinfo_day e where e.company_id = #{companyId} and e.energyType = #{type} and e.accnumber =
#{consNo}

    and DATE_FORMAT(e.date,'%Y-%m') = #{date}


    and PERIOD_DIFF(date_format(STR_TO_DATE(#{date}, '%Y-%m'),'%Y%m'),date_format(e.date,'%Y%m')) = 12

GROUP BY e.date ORDER BY e.date

mapper.update:
update t_enterpriseinfo set
entName = #{entName}

    ,creditCode=#{creditCode}


    ,legalPerson=#{legalPerson}


    ,registerDate=#{registerDate}


    ,address=#{address}


    ,areaCode=#{areaCode}


    ,registrationNumber=#{registrationNumber}


    ,industry=#{industry}


    ,industryName=#{industryName}


    ,area=#{area}


    ,entType=#{entType}


    ,investment=#{investment}


    ,areaName=#{areaName}


    ,peopleNum=#{peopleNum}


    ,built_area=#{builtArea}


    ,covered_area=#{coveredArea}


    ,micro_park=#{microPark}


    ,lng=#{lng}


    ,lat=#{lat}

where company_id = #{companyId}

mapper.select:
SELECT DATE_FORMAT(aa.date,'%Y-%m') date, TRUNCATE((aa.value - bb.value)/bb.value * 100, 2) rate, TRUNCATE(aa.value,2) thisValue, TRUNCATE(bb.value,2) lastValue, TRUNCATE(aa.ecValue,2) thisEcValue, TRUNCATE(bb.ecValue,2) lastEcValue, TRUNCATE((aa.value/aa.ecValue),2) thisEcValueRate, TRUNCATE((bb.value/bb.ecValue),2) lastEcValueRate from (SELECT t.date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value, IFNULL(TRUNCATE(SUM(ec.value),2),0) ecValue from t_enterpriseinfo ent LEFT JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year} LEFT JOIN t_ent_economicsinfo ec ON ent.company_id = ec.company_id and t.date = ec.date and t.date = ec.date where 1=1
and ent.areaLabel = #{areaLabel}
and ent.micro_park = #{microPark}
and ent.company_id = #{companyId}
GROUP BY DATE_FORMAT(t.date,'%Y-%m') ORDER BY DATE_FORMAT(t.date,'%Y-%m'))aa, (SELECT t.date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value, IFNULL(TRUNCATE(SUM(ec.value),2),0) ecValue from t_enterpriseinfo ent LEFT JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year} - 1 LEFT JOIN t_ent_economicsinfo ec ON ent.company_id = ec.company_id and t.date = ec.date and t.date = ec.date where 1=1
and ent.areaLabel = #{areaLabel}
and ent.micro_park = #{microPark}
and ent.company_id = #{companyId}
GROUP BY DATE_FORMAT(t.date,'%Y-%m') ORDER BY DATE_FORMAT(t.date,'%Y-%m'))bb where DATE_FORMAT(aa.date,'%m') = DATE_FORMAT(bb.date,'%m')

SELECT DATE_FORMAT(aa.date,'%Y-%m') date , TRUNCATE(aa.value,2) thisValue, TRUNCATE(aa.ecValue,2) thisEcValue, TRUNCATE((aa.value/aa.ecValue),2) thisEcValueRate , TRUNCATE(bb.value,2) lastValue, TRUNCATE(bb.ecValue,2) lastEcValue, TRUNCATE((bb.value/bb.ecValue),2) lastEcValueRate , TRUNCATE(aa.value/bb.value - 1,2) rate from (SELECT t.date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value, IFNULL(TRUNCATE(SUM(ec.value),2),0) ecValue from t_enterpriseinfo ent INNER JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year} LEFT JOIN t_ent_economicsinfo ec ON ent.company_id = ec.company_id and t.date = ec.date and t.date = ec.date where 1=1
and ent.areaLabel = #{areaLabel}
and ent.micro_park = #{microPark}
and ent.company_id = #{companyId}
GROUP BY DATE_FORMAT(t.date,'%Y-%m') ORDER BY DATE_FORMAT(t.date,'%Y-%m'))aa LEFT JOIN (SELECT t.date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value, IFNULL(TRUNCATE(SUM(ec.value),2),0) ecValue from t_enterpriseinfo ent INNER JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year} - 1 LEFT JOIN t_ent_economicsinfo ec ON ent.company_id = ec.company_id and t.date = ec.date and t.date = ec.date where 1=1
and ent.areaLabel = #{areaLabel}
and ent.micro_park = #{microPark}
and ent.company_id = #{companyId}
GROUP BY DATE_FORMAT(t.date,'%Y-%m') ORDER BY DATE_FORMAT(t.date,'%Y-%m'))bb ON MONTH(aa.date) = MONTH(bb.date) ORDER BY DATE_FORMAT(aa.date,'%Y-%m')

SELECT aa.date, IFNULL(TRUNCATE(aa.value/bb.value,2),0) value from
(SELECT DATE_FORMAT(t.date,'%Y') date, SUM(t.tce) value
from t_enterpriseinfo ent, t_ent_energyinfo_comp_sum t
where ent.company_id = t.company_id

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{microPark}


    and ent.company_id = #{companyId}

GROUP BY DATE_FORMAT(t.date,'%Y')
ORDER BY DATE_FORMAT(t.date,'%Y'))aa
,
(SELECT DATE_FORMAT(ec.date,'%Y') date,sum(ec.value) value
from t_enterpriseinfo ent, t_ent_economicsinfo ec
where ent.company_id = ec.company_id

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{microPark}


    and ent.company_id = #{companyId}

GROUP BY DATE_FORMAT(ec.date,'%Y')
ORDER BY DATE_FORMAT(ec.date,'%Y'))bb
where aa.date = bb.date

SELECT aa.date date, TRUNCATE((aa.value - bb.value)/bb.value * 100, 2) rate,
TRUNCATE(aa.value,2) thisValue, TRUNCATE(bb.value,2) lastValue, TRUNCATE(cc.ecValue,2) thisEcValue,
TRUNCATE(dd.ecValue,2) lastEcValue,
TRUNCATE((aa.value/cc.ecValue),2) thisEcValueRate, TRUNCATE((bb.value/dd.ecValue),2) lastEcValueRate from
(SELECT QUARTER(t.date) date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value
from t_enterpriseinfo ent
LEFT JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year}
where 1=1

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{microPark}


    and ent.company_id = #{companyId}

GROUP BY QUARTER(t.date)
ORDER BY QUARTER(t.date))aa
LEFT JOIN
(SELECT QUARTER(ec.date) date, IFNULL(TRUNCATE(SUM(ec.value),2),0) ecValue
from t_enterpriseinfo ent
LEFT JOIN t_ent_economicsinfo ec ON ent.company_id = ec.company_id and YEAR(ec.date) = #{year}
where 1=1

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{microPark}


    and ent.company_id = #{companyId}

GROUP BY QUARTER(ec.date)
ORDER BY QUARTER(ec.date))cc ON aa.date = cc.date
LEFT JOIN
(SELECT QUARTER(t.date) date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value
from t_enterpriseinfo ent
LEFT JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year} - 1
where 1=1

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{microPark}


    and ent.company_id = #{companyId}

GROUP BY QUARTER(t.date)
ORDER BY QUARTER(t.date))bb on aa.date = bb.date
LEFT JOIN
(SELECT QUARTER(ec.date) date, IFNULL(TRUNCATE(SUM(ec.value),2),0) ecValue
from t_enterpriseinfo ent
LEFT JOIN t_ent_economicsinfo ec ON ent.company_id = ec.company_id and YEAR(ec.date) = #{year} - 1
where 1=1

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{microPark}


    and ent.company_id = #{companyId}

GROUP BY QUARTER(ec.date)
ORDER BY QUARTER(ec.date))dd on aa.date = dd.date
where aa.date is not NULL

SELECT c.remark, IFNULL(TRUNCATE(SUM(aa.tce),2),0) value
from t_config c
LEFT JOIN (SELECT t.energyType, t.tce from t_ent_energyinfo_comp_sum t, t_enterpriseinfo ent where YEAR(t.date)
= #{year} and t.company_id = ent.company_id

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{microPark}


    and ent.company_id = #{companyId}

) aa on c.value = aa.energyType
where c.type = 'energyType'
GROUP BY c.value
ORDER BY value desc

SELECT DATE_FORMAT(aa.date,'%Y-%m') date, TRUNCATE((aa.value - bb.value)/bb.value * 100, 2) rate, TRUNCATE(aa.value,2) thisValue, TRUNCATE(bb.value,2) lastValue, TRUNCATE(aa.ecValue,2) thisEcValue, TRUNCATE(bb.ecValue,2) lastEcValue, TRUNCATE((aa.value/aa.ecValue),2) thisEcValueRate, TRUNCATE((bb.value/bb.ecValue),2) lastEcValueRate from (SELECT t.date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value, sum(ec.value) ecValue from t_area_statistics t LEFT JOIN t_area_statistics ec ON t.areaCode = ec.areaCode and t.date = ec.date and ec.energyType = 'ec' where YEAR(t.date) = #{year} and t.energyType != 'ec' and t.areaCode = #{areaCode}
and t.energyType = #{energyType}
GROUP BY DATE_FORMAT(t.date,'%Y-%m') ORDER BY DATE_FORMAT(t.date,'%Y-%m'))aa, (SELECT t.date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value, sum(ec.value) ecValue from t_area_statistics t LEFT JOIN t_area_statistics ec ON t.areaCode = ec.areaCode and t.date = ec.date and ec.energyType = 'ec' where YEAR(t.date) = #{year} - 1 and t.energyType != 'ec' and t.areaCode = #{areaCode}
and t.energyType = #{energyType}
GROUP BY DATE_FORMAT(t.date,'%Y-%m') ORDER BY DATE_FORMAT(t.date,'%Y-%m'))bb where DATE_FORMAT(aa.date,'%m') = DATE_FORMAT(bb.date,'%m')

SELECT DATE_FORMAT(aa.date,'%Y-%m') date, TRUNCATE(aa.value,2) thisValue, TRUNCATE(aa.ecValue,2) thisEcValue, TRUNCATE(lastValue,2) lastValue, TRUNCATE(lastEcValue,2) lastEcValue, TRUNCATE((aa.value/aa.ecValue),2) thisEcValueRate from (SELECT t.date, IFNULL(TRUNCATE(SUM(t.tce),2),0) value, sum(ec.value) ecValue, IFNULL(TRUNCATE(SUM(last.tce),2),0) lastValue, sum(lastec.value) lastEcValue from t_area_statistics t LEFT JOIN t_area_statistics ec ON t.areaCode = ec.areaCode and t.date = ec.date and ec.energyType = 'ec' LEFT JOIN t_area_statistics last ON t.areaCode = last.areaCode and last.date = DATE_SUB(t.date,INTERVAL 12 MONTH) and last.energyType = t.energyType LEFT JOIN t_area_statistics lastec ON t.areaCode = lastec.areaCode and lastec.date = DATE_SUB(t.date,INTERVAL 12 MONTH) and lastec.energyType = 'ec' where YEAR(t.date) = #{year} and t.energyType != 'ec' and t.areaCode = #{areaCode}
and t.energyType = #{energyType}
GROUP BY DATE_FORMAT(t.date,'%Y-%m') ORDER BY DATE_FORMAT(t.date,'%Y-%m'))aa

SELECT DATE_FORMAT(t.date, '%Y') date, IFNULL(TRUNCATE(SUM(t.tce), 2), 0) value, IFNULL(TRUNCATE(SUM(ec.value), 2), 0) ecValue, TRUNCATE(IFNULL(TRUNCATE(SUM(t.tce), 2), 0) / IFNULL(TRUNCATE(SUM(ec.value), 2), null), 4) rate FROM t_area_statistics t LEFT JOIN t_area_statistics ec ON t.areaCode = ec.areaCode and ec.energyType = 'ec' and t.date = ec.date WHERE 1=1 AND t.areaCode = #{areaCode} and t.energyType != 'ec'
and t.energyType = #{energyType}
GROUP BY DATE_FORMAT(t.date, '%Y') ORDER BY DATE_FORMAT(t.date, '%Y')

SELECT c.remark, IFNULL(TRUNCATE(SUM(t.tce),2),0) value from t_config c LEFT JOIN t_area_statistics t on c.value = t.energyType and YEAR(t.date) = #{year}
and t.areaLabel = #{areaLabel}
where c.type = 'energyType' and c.value != 3 GROUP BY c.value ORDER BY value desc

select c1.value areaCode, c1.remark areaName , IFNULL(SUM(p.thisvalue),0) sumValue , round(IFNULL(((SUM(p.thisvalue)/SUM(p.lastvalue))-1) * 100, 0), 3) rate , round(IFNULL(((SUM(p.thisvalue)/SUM(p.lastYearValue))-1) * 100, 0), 3) sameRate , count(if(production_status=4,true,null)) increaseCount , count(if(growthRate=0,true,null)) decreaseCount , IFNULL(SUM(isZhiliang),0) isZhiliang , IFNULL(SUM(isKechuang),0) isKechuang , IFNULL(SUM(isGaoxin),0) isGaoxin , IFNULL(SUM(isRegulation),0) isRegulation , ROUND(IFNULL((SELECT SUM(tt.`value`) from t_area_statistics tt where tt.areaCode = c1.value and YEAR(tt.date) = YEAR(NOW())), 0), 3) yearValue from t_config c1 LEFT JOIN (SELECT e.company_id , s.value thisvalue , s1.value lastvalue , s2.value lastYearValue , CASE when (s.value/s1.value -1) *100 - c.value > 0 then 1 else 0 END rate , s.production_status , s.growth_rate growthRate, e.unit_ele, e.areaCode, e.areaName , CASE WHEN r.tag_key IS NOT NULL then 1 else 0 END isRegulation , CASE WHEN r1.tag_key IS NOT NULL then 1 else 0 END isZhiliang , CASE WHEN r2.tag_key IS NOT NULL then 1 else 0 END isKechuang , CASE WHEN r3.tag_key IS NOT NULL then 1 else 0 END isGaoxin from t_config c, t_enterpriseinfo e LEFT JOIN t_ent_tag_rel r on e.company_id = r.identifier and r.tag_key = 'regulation' LEFT JOIN t_ent_tag_rel r1 on e.company_id = r1.identifier and r1.tag_key = 'zhiliang' LEFT JOIN t_ent_tag_rel r2 on e.company_id = r2.identifier and r2.tag_key = 'kechuang' LEFT JOIN t_ent_tag_rel r3 on e.company_id = r3.identifier and r3.tag_key = 'gaoxin' LEFT JOIN t_ent_energyinfo_comp_sum s ON e.company_id = s.company_id and s.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH) ,'%Y-%m'),'-01 00:00:00') and s.energyType = 0 LEFT JOIN t_ent_energyinfo_comp_sum s1 ON s1.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 MONTH) ,'%Y-%m'),'-01 00:00:00') and s.company_id = s1.company_id and s1.energyType = 0 LEFT JOIN t_ent_energyinfo_comp_sum s2 ON s2.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 13 MONTH) ,'%Y-%m'),'-01 00:00:00') and s.company_id = s2.company_id and s2.energyType = 0 where e.areaLabel = c.areaLabel and c.type = 'insightIndex' and c.subTypeName = 'stable'
and c.areaLabel = #{areaLabel}
and e.areaCode is not NULL ) p ON c1.value = p.areaCode where c1.type = 'areaCode'
and c1.areaLabel = #{areaLabel}
GROUP BY c1.value order by c1.order_flag

SELECT aa.date, (aa.value - bb.value)/bb.value * 100, aa.value thisValue, bb.value lastValue from
(SELECT MONTH(t.date) date, IFNULL(TRUNCATE(SUM(t.tce),2),0)/sum(ec.value) value
from t_enterpriseinfo ent
LEFT JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year}
LEFT JOIN t_ent_economicsinfo ec ON t.company_id = ec.company_id and YEAR(ec.date) = #{year}
where 1=1

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{key}


    and ent.company_id = #{companyId}

GROUP BY MONTH(t.date)
ORDER BY MONTH(t.date)) aa,
(SELECT MONTH(t.date) date, IFNULL(TRUNCATE(SUM(t.tce),2),0)/sum(ec.value) value
from t_enterpriseinfo ent
LEFT JOIN t_ent_energyinfo_comp_sum t on ent.company_id = t.company_id and YEAR(t.date) = #{year} -1
LEFT JOIN t_ent_economicsinfo ec ON t.company_id = ec.company_id and YEAR(ec.date) = #{year} -1
where 1=1

    and ent.areaLabel = #{areaLabel}


    and ent.micro_park = #{key}


    and ent.company_id = #{companyId}

GROUP BY MONTH(t.date)
ORDER BY MONTH(t.date)) bb
where aa.date = bb.date

SELECT aa.datelist, aa.company_id companyId, aa.name entName, aa.accnumber , aa.creditCode,aa.orgCode,aa.areaName,aa.address,aa.micro_park,aa.business,aa.contactPerson,aa.contactPhone,aa.industryTypeName,aa.output_value,aa.accNumberList,aa.tagList , d.value from (SELECT datelist, t.company_id, e.entName name, t.accnumber , e.creditCode,e.orgCode,e.areaName,e.address,e.micro_park,e.business,e.contactPerson,e.contactPhone,e.industryTypeName , conf.remark output_value , GROUP_CONCAT(DISTINCT r.accNumber) accNumberList , GROUP_CONCAT(DISTINCT tag.tag_name) tagList from calendar c, t_ent_accnumber_rel t, t_enterpriseinfo e LEFT JOIN t_config conf on conf.type = 'outputValue' and conf.`value` = e.output_value LEFT JOIN t_ent_accnumber_rel r ON r.company_id = e.company_id and r.type = '0' LEFT JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id LEFT JOIN t_tag tag ON tr.tag_key = tag.tag_key
where c.datelist >= #{startDate} and c.datelist <= #{endDate}
and e.company_id = t.company_id and t.type = 0
and e.areaLabel = #{areaLabel}
and e.company_id = #{companyId}
GROUP BY c.datelist, t.accnumber ) aa LEFT JOIN t_ent_energyinfo_day d ON d.date = aa.datelist and aa.company_id = d.company_id AND aa.accnumber = d.accnumber and d.energyType = 0

SELECT SUBSTR(aa.datelist, 1, 7) datelist, aa.company_id companyId, aa.name entName, aa.accnumber , aa.creditCode,aa.orgCode,aa.areaName,aa.address,aa.micro_park,aa.business,aa.contactPerson,aa.contactPhone,aa.industryTypeName,aa.output_value,aa.accNumberList,aa.tagList , d.value from (SELECT CONCAT(DATE_FORMAT(datelist,'%Y-%m'),'-01') datelist, t.company_id, e.entName name, t.accnumber , e.creditCode,e.orgCode,e.areaName,e.address,e.micro_park,e.business,e.contactPerson,e.contactPhone,e.industryTypeName , conf.remark output_value , GROUP_CONCAT(DISTINCT r.accNumber) accNumberList , GROUP_CONCAT(DISTINCT tag.tag_name) tagList from calendar c, t_ent_accnumber_rel t, t_enterpriseinfo e LEFT JOIN t_config conf on conf.type = 'outputValue' and conf.`value` = e.output_value LEFT JOIN t_ent_accnumber_rel r ON r.company_id = e.company_id and r.type = '0' LEFT JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id LEFT JOIN t_tag tag ON tr.tag_key = tag.tag_key
where c.datelist >= #{startDate} and c.datelist <= #{endDate} and e.company_id = t.company_id and t.type = 0
and e.areaLabel = #{areaLabel}
and e.company_id = #{companyId}
GROUP BY DATE_FORMAT(datelist,'%Y-%m'),t.accnumber) aa LEFT JOIN t_ent_energyinfo d ON d.date = aa.datelist AND aa.company_id = d.company_id AND aa.accnumber = d.accnumber and d.energyType = 0

