and e.areaLabel = #{areaLabel}
and i.date = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m-01')
and e.areaCode = #{areaCode}
and (r.accnumber like concat('%', #{key}, '%') or e.entName like concat('%', #{key}, '%'))
ORDER BY i.value DESC

