where 1=1
and e.areaLabel = #{areaLabel}
and SUBSTR(e.industryType,1,1) = 'C'
and e.areaCode = #{areaCode}
GROUP BY CONCAT(SUBSTR(e.industryType,1,3) ,'00')) b ON i.`code` = b.`code` INNER JOIN (SELECT CONCAT(SUBSTR(e.industryType,1,3) ,'00') `code`, SUM(eng.value) `value` from t_enterpriseinfo e LEFT JOIN t_ent_energyinfo_comp_sum eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 13 MONTH),'%Y-%m') and eng.energyType = 0
INNER JOIN t_ent_tag_rel r ON r.identifier = e.company_id and r.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and SUBSTR(e.industryType,1,1) = 'C'
and e.areaCode = #{areaCode}
GROUP BY CONCAT(SUBSTR(e.industryType,1,3) ,'00')) c ON i.`code` = c.`code` ORDER BY a.`value` desc

