and e.company_id = r.identifier and r.tag_key = 'follow' GROUP BY e.company_id) a ON i.company_id = a.`code` INNER JOIN (SELECT e.company_id `code`, SUM(eng.value) `value` from t_ent_tag_rel r, t_enterpriseinfo e LEFT JOIN t_ent_energyinfo_comp_sum eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 MONTH),'%Y-%m') and eng.energyType = 0
