from t_ent_energyinfo_comp_sum eng, t_enterpriseinfo e

    INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}

where 1=1 and e.company_id = eng.company_id and eng.energyType = 0 and eng.date >=
CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 12 MONTH),'%Y-%m'),'-01 00:00:00')

    and e.areaLabel = #{areaLabel}


    and e.areaCode = #{areaCode}

and eng.growth_rate = #{growth}
GROUP BY eng.growth_rate, eng.date

SELECT eng.production_status, DATE_FORMAT(eng.date,'%Y-%m-%d') date, count(eng.company_id) value
from t_ent_energyinfo_comp_sum eng, t_enterpriseinfo e

    INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}

where 1=1 and e.company_id = eng.company_id and eng.energyType = 0 and eng.date >=
CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 12 MONTH),'%Y-%m'),'-01 00:00:00')

    and e.areaLabel = #{areaLabel}


    and e.areaCode = #{areaCode}

and eng.production_status = #{production}
GROUP BY eng.production_status, eng.date

SELECT e.company_id, e.entName, IFNULL(ROUND(e.unit_ele,2),'') unitEle, ROUND(s.value,2) value, ROUND(s.tce,2) tce, IFNULL(e.output_value,'-1') outputValue, IFNULL(e.areaName,'') areaName, (SELECT name From t_industry_category where `code` = CONCAT(SUBSTR(e.industryType, 1,3 ) ,'00')) industry, IFNULL(s.growth_rate,'-1') growthRate, IFNULL(ROUND(s.month_rate,2),0) monthRate, IFNULL(ROUND(s.year_rate,2),0) yearRate, IFNULL(s.production_status,'-1') productionStatus, IFNULL(ROUND(s.production_value,2),'') productionValue , (SELECT ROUND(sum(a.value),2) from t_ent_energyinfo_comp_sum a where e.company_id = a.company_id and YEAR(a.date) = SUBSTR(#{date}, 1,4)) yearValue ,(SELECT c.remark from t_config c where c.type = 'outputValue' and e.output_value = c.value) outputValueName ,CASE WHEN production_status = 0 THEN '' WHEN production_status = 1 THEN '' WHEN production_status = 2 THEN '' WHEN production_status = 3 THEN '' WHEN production_status = 4 THEN '' WHEN production_status = 5 THEN '' else '' end productionStatusName ,CASE WHEN growth_rate = 0 THEN '' WHEN growth_rate = 1 THEN '' WHEN growth_rate = 2 THEN '' WHEN growth_rate = 3 THEN '' else '' end growthRateName from t_enterpriseinfo e LEFT JOIN t_ent_energyinfo_comp_sum s ON e.company_id = s.company_id and s.date = #{date} where 1=1
and e.areaLabel = #{areaLabel}
and e.entName like concat('%', #{key}, '%')

