  from t_ent_energyinfo_comp_week_sum
 where  monday>= #{startTime}

  and  monday<= #{endTime}

delete from t_area_statistics
      where DATE_FORMAT(date,'%Y-%m')= #{date}
      and energyType = #{dataType}

delete from t_area_statistics
 where DATE_FORMAT(date,'%Y-%m') = #{date}
    and energyType = #{dataType}

delete
  from `t_ent_energyinfo_comp_sum`
   where year(date) = #{date}
  		and energyType = #{dataType}

delete from t_area_statistics
where year(date)= #{date}
 and energyType = #{dataType}

delete
 from `t_ent_energyinfo_comp_sum`
 where date = #{date}
 	and company_id = #{companyId}

mapper.insert:
INSERT INTO `t_ent_energyinfo_comp_week_sum` (`company_id`, `entName`, `orgCode`, `energyType`, `value`, `monday`, `week_num`, `tce`, `week_rate`, `production_status`, `production_value`, `growth_rate`) SELECT d.company_id, d.entName, d.orgCode, d.energyType, SUM(d.`value`) value, date_sub(d.date,INTERVAL WEEKDAY(d.date) DAY) Monday, WEEKOFYEAR(d.date) week_num , NULL tce, NULL week_rate, NULL production_status , CASE when (SELECT SUM(r.unit_ele_day) FROM t_ent_accnumber_rel r where r.type = 0 and d.company_id = r.company_id) IS NOT NULL THEN SUM(d.`value`)/(SELECT SUM(ifnull(r.unit_ele_day,'')) * 7 FROM t_ent_accnumber_rel r where r.type = 0 and d.company_id = r.company_id) * 100 ELSE NULL END production_value , NULL growth_rate from t_ent_energyinfo_day d where d.energyType = 0 and value != 0
and d.date <= #{endTime} and d.date >= #{startTime}
GROUP BY d.company_id, WEEKOFYEAR(d.date) ORDER BY production_value

 INSERT INTO `t_ent_energyinfo_comp_sum` (`company_id`, `entName`, `orgCode`, `energyType`, `value`, `date`, `tce`)
SELECT t.company_id, t.entName, NULL,#{dataType}, SUM(t.`value`), t.date, SUM(t.`value`)*#{tce}
 from t_ent_energyinfo t
 WHERE t.energyType = #{dataType}
    and DATE_FORMAT(t.date,'%Y-%m') = #{date}
	GROUP BY t.company_id  ,t.date

 		INSERT INTO `t_ent_energyinfo_comp_sum` (`company_id`, `entName`, `orgCode`, `energyType`, `value`, `date`, `tce`)
		SELECT t.company_id, t.entName, NULL, #{dataType}, SUM(t.`value`), t.date, SUM(t.`value`)* #{tce}
		 from t_ent_energyinfo t
		 WHERE t.energyType = #{dataType}
		    and year(date)= #{date}
			GROUP BY t.company_id  ,t.date

 INSERT INTO `t_ent_energyinfo_comp_sum` (`company_id`, `entName`, `orgCode`, `energyType`, `value`, `date`, `tce`)
SELECT t.company_id, t.entName, NULL,t.energyType, SUM(t.`value`), t.date, SUM(t.tce)
 from t_ent_energyinfo t
 WHERE  t.date = #{date}
	and company_id = #{companyId}
	group By energyType

mapper.select:
SELECT t.value month
, TRUNCATE(a.value,2) thisValue, TRUNCATE(b.value,2) lastValue
, TRUNCATE((a.value/b.value - 1) * 100,2) rate
, TRUNCATE(c.value,2) thisTceValue, TRUNCATE(d.value,2) lastTceValue
, TRUNCATE((c.value/a.value),4) thisOutputValue, TRUNCATE((d.value/b.value),4) lastOutputValue
, TRUNCATE(((c.value/a.value)/(d.value/b.value) - 1) * 100,2) outputValueRate
from t_time t
LEFT JOIN
(SELECT eng.date, SUM(eng.value) value from t_enterpriseinfo e, t_ent_economicsinfo eng

    INNER JOIN t_ent_tag_rel tr ON tr.identifier = eng.company_id and tr.tag_key = #{type}

where 1=1 and e.company_id = eng.company_id and YEAR(eng.date) = YEAR(NOW())

    and e.areaLabel = #{areaLabel}


    and e.areaCode = #{areaCode}

GROUP BY eng.date) a ON t.`value` = MONTH(a.date)
LEFT JOIN
(SELECT eng.date, SUM(eng.value) value from t_enterpriseinfo e, t_ent_economicsinfo eng

    INNER JOIN t_ent_tag_rel tr ON tr.identifier = eng.company_id and tr.tag_key = #{type}

where 1=1 and e.company_id = eng.company_id and YEAR(eng.date) = YEAR(DATE_SUB(NOW(),INTERVAL 12 MONTH))

    and e.areaLabel = #{areaLabel}


    and e.areaCode = #{areaCode}

GROUP BY eng.date) b ON t.`value` = MONTH(b.date)
LEFT JOIN
(SELECT eng.date, SUM(eng.tce) value from t_enterpriseinfo e, t_ent_energyinfo_comp_sum eng

    INNER JOIN t_ent_tag_rel tr ON tr.identifier = eng.company_id and tr.tag_key = #{type}

where 1=1 and e.company_id = eng.company_id and YEAR(eng.date) = YEAR(NOW()) and eng.energyType = 0

    and e.areaLabel = #{areaLabel}


    and e.areaCode = #{areaCode}

GROUP BY eng.date) c ON t.`value` = MONTH(c.date)
LEFT JOIN
(SELECT eng.date, SUM(eng.tce) value from t_enterpriseinfo e, t_ent_energyinfo_comp_sum eng

    INNER JOIN t_ent_tag_rel tr ON tr.identifier = eng.company_id and tr.tag_key = #{type}

where 1=1 and e.company_id = eng.company_id and YEAR(eng.date) = YEAR(DATE_SUB(NOW(),INTERVAL 12 MONTH)) and
eng.energyType = 0

    and e.areaLabel = #{areaLabel}


    and e.areaCode = #{areaCode}

GROUP BY eng.date) d ON t.`value` = MONTH(d.date)
where t.type = 1

SELECT e.company_id, e.entName
from t_ent_energyinfo_comp_sum s, t_enterpriseinfo e
where s.company_id = e.company_id

    and e.areaLabel = #{areaLabel}

and s.date = DATE_FORMAT(DATE_SUB(now(),INTERVAL 1 MONTH),'%Y-%m-01 00:00:00')
and s.production_status = #{productionStatus}

SELECT i.`code`, i.`name`, a.coveredArea, ROUND(a.value/a.coveredArea,2) outputValue, ROUND(IFNULL((a.`value`/b.`value` - 1),0) * 100,0) yearrate from t_industry_category i INNER JOIN (SELECT CONCAT(SUBSTR(e.industryType,1,3) ,'00') `code`, SUM(e.covered_area) coveredArea, SUM(eng.value) `value` from t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m')
INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
and SUBSTR(e.industryType,1,1) = 'C' GROUP BY CONCAT(SUBSTR(e.industryType,1,3) ,'00')) a ON i.`code` = a.`code` INNER JOIN (SELECT CONCAT(SUBSTR(e.industryType,1,3) ,'00') `code`, SUM(eng.value) `value` from t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 13 MONTH),'%Y-%m')
INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
and SUBSTR(e.industryType,1,1) = 'C' GROUP BY CONCAT(SUBSTR(e.industryType,1,3) ,'00')) b ON i.`code` = b.`code` ORDER BY a.`value` desc

SELECT i.tag_key `code`, i.tag_name `name`, a.coveredArea, ROUND(a.value/a.coveredArea,2) outputValue, IFNULL(a.`value`,0) thisValue, IFNULL(b.`value`,0) lastValue, IFNULL(c.`value`,0) lastYearValue, IFNULL((a.`value`/b.`value` - 1),0) * 100 samerate, IFNULL((a.`value`/c.`value` - 1),0) * 100 yearrate from t_tag i INNER JOIN (SELECT r.tag_key `code`, SUM(e.covered_area) coveredArea, SUM(eng.value) `value` from t_ent_tag_rel r, t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m')
INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
and e.company_id = r.identifier and r.tag_key in ('regulation','lingJun','zhanXin','jingKai','digitalEC','equipment','technology','newProduct','toRegular') GROUP BY r.tag_key) a ON i.tag_key = a.`code` INNER JOIN (SELECT r.tag_key `code`, SUM(e.covered_area) coveredArea, SUM(eng.value) `value` from t_ent_tag_rel r, t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 MONTH),'%Y-%m')
INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
and e.company_id = r.identifier and r.tag_key in ('regulation','lingJun','zhanXin','jingKai','digitalEC','equipment','technology','newProduct','toRegular') GROUP BY r.tag_key) b ON i.tag_key = b.`code` INNER JOIN (SELECT r.tag_key `code`, SUM(e.covered_area) coveredArea, SUM(eng.value) `value` from t_ent_tag_rel r, t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 13 MONTH),'%Y-%m')
INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
and e.company_id = r.identifier and r.tag_key in ('regulation','lingJun','zhanXin','jingKai','digitalEC','equipment','technology','newProduct','toRegular') GROUP BY r.tag_key) c ON i.tag_key = c.`code` where i.attribute = 'public' or i.areaLabel = #{areaLabel} ORDER BY a.`value` desc

SELECT i.`value` `code`, i.remark `name`, a.coveredArea, ROUND(a.value/a.coveredArea,2) outputValue, IFNULL(a.`value`,0) thisValue, IFNULL(b.`value`,0) lastValue, IFNULL(c.`value`,0) lastYearValue, IFNULL((a.`value`/b.`value` - 1),0) * 100 samerate, IFNULL((a.`value`/c.`value` - 1),0) * 100 yearrate from t_config i INNER JOIN (SELECT e.output_value `code`, SUM(e.covered_area) coveredArea, SUM(eng.value) `value` from t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH),'%Y-%m')
