where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
GROUP BY e.output_value) a ON i.`value` = a.`code` INNER JOIN (SELECT e.output_value `code`, SUM(e.covered_area) coveredArea, SUM(eng.value) `value` from t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 MONTH),'%Y-%m')
INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
GROUP BY e.output_value) b ON i.`value` = b.`code` INNER JOIN (SELECT e.output_value `code`, SUM(e.covered_area) coveredArea, SUM(eng.value) `value` from t_enterpriseinfo e LEFT JOIN t_ent_economicsinfo eng on e.company_id = eng.company_id and DATE_FORMAT(eng.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 13 MONTH),'%Y-%m')
INNER JOIN t_ent_tag_rel tr ON tr.identifier = e.company_id and tr.tag_key = #{type}
where 1=1
and e.areaLabel = #{areaLabel}
and e.areaCode = #{areaCode}
GROUP BY e.output_value) c ON i.`value` = c.`code` where i.type = 'outputValue' ORDER BY a.`value` desc

