and e.areaCode = #{areaCode}
and e.company_id = r.identifier and r.tag_key = 'follow' GROUP BY e.company_id) c ON i.company_id = c.`code` ORDER BY a.`value` desc

select c1.value areaCode, c1.remark areaName , round(IFNULL(SUM(p.thisvalue),0), 2) sumValue , round(IFNULL(SUM(p.thisECvalue),0), 2) sumECValue , round(IFNULL(((SUM(p.thisTcevalue)/SUM(p.thisECvalue))), 0), 3) ecRate , round(IFNULL(((SUM(p.lastTcevalue)/SUM(p.lastECvalue))), 0), 3) eclastRate , round(IFNULL(((SUM(p.thisTcevalue)/SUM(p.peopleNum))), 0), 3) peopleRate , round(IFNULL(((SUM(p.thisTcevalue)/SUM(p.covered_area))), 0), 3) coveredRate , round(IFNULL(((SUM(p.thisECvalue)/SUM(p.covered_area))), 0), 3) coveredECRate , round(IFNULL((((SUM(p.thisvalue)-SUM(p.lastvalue))/SUM(p.lastvalue))) * 100, 0), 3) monthRate , round(IFNULL(((SUM(p.thisvalue)/SUM(p.unit_ele))) * 100, 0), 3) yearRate , round(IFNULL((((SUM(p.thisECvalue)-SUM(p.lastECvalue))/SUM(p.lastECvalue))) * 100, 0), 3) ecMonthRate , round(IFNULL((((SUM(p.thisECvalue)-SUM(p.yearECvalue))/SUM(p.yearECvalue))) * 100, 0), 3) ecYearRate , round(IFNULL((((SUM(p.thisECvalue)-SUM(p.yearECvalue))/SUM(p.peopleNum))) * 100, 0), 3) peopleYearRate , round(IFNULL((((SUM(p.thisECvalue)-SUM(p.lastTcevalue))/SUM(p.covered_area))) * 100, 0), 3) coveredMonthRate , round(IFNULL((((SUM(p.thisTcevalue)-SUM(p.lastTcevalue))/SUM(p.thisECvalue))) * 100, 0), 3) ecMonthRate , round(IFNULL(1-(SUM(p.lastTcevalue)*SUM(p.thisECvalue))/(SUM(p.lastECvalue)*SUM(p.thisTcevalue)),0) * 100, 3) tceECMonthRate , IFNULL(SUM(isRegulation),0) isRegulation , IFNULL(SUM(peopleNum),0) sumPeopleNum , round(IFNULL(SUM(covered_area),0), 2) sumCoveredArea , IFNULL(SUM(tceECMonthRateFlag),0) tceECMonthRateFlag , IFNULL(SUM(monthRateFlag),0) monthRateFlag from t_config c1 LEFT JOIN (SELECT e.company_id , s.value thisvalue , s1.value lastvalue , s2.value lastYearvalue , s.tce thisTcevalue , s1.tce lastTcevalue , v1.value thisECvalue , v2.value lastECvalue , v3.value yearECvalue , s.production_status , s.growth_rate growthRate, e.unit_ele, e.areaCode, e.areaName , CASE WHEN r.tag_key IS NOT NULL then 1 else 0 END isRegulation , e.peopleNum , e.covered_area
, CASE WHEN IFNULL(1-(s1.tce*v1.value)/(v2.value*s.tce),0) * 100 > 25 THEN 1 WHEN IFNULL(1-(s1.tce*v1.value)/(v2.value*s.tce),0) * 100 < -25 THEN 1 ELSE 0 END tceECMonthRateFlag , CASE WHEN s.month_rate > 25 THEN 1 WHEN s.month_rate < -25 THEN 1 ELSE 0 END monthRateFlag
from t_enterpriseinfo e LEFT JOIN t_ent_tag_rel r on e.company_id = r.identifier and r.tag_key = 'regulation' LEFT JOIN t_ent_energyinfo_comp_sum s ON e.company_id = s.company_id and s.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH) ,'%Y-%m'),'-01 00:00:00') and s.energyType = 0 LEFT JOIN t_ent_energyinfo_comp_sum s1 ON s1.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 MONTH) ,'%Y-%m'),'-01 00:00:00') and e.company_id = s1.company_id and s1.energyType = 0 LEFT JOIN t_ent_energyinfo_comp_sum s2 ON s2.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 13 MONTH) ,'%Y-%m'),'-01 00:00:00') and e.company_id = s2.company_id and s2.energyType = 0 LEFT JOIN t_ent_economicsinfo v1 ON e.company_id = v1.company_id and v1.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 MONTH) ,'%Y-%m'),'-01 00:00:00') LEFT JOIN t_ent_economicsinfo v2 ON v2.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 MONTH) ,'%Y-%m'),'-01 00:00:00') and e.company_id = v2.company_id LEFT JOIN t_ent_economicsinfo v3 ON v3.date = CONCAT(DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 13 MONTH) ,'%Y-%m'),'-01 00:00:00') and e.company_id = v3.company_id where e.areaCode is not NULL
and e.areaLabel = #{areaLabel}
) p ON c1.value = p.areaCode where c1.type = 'areaCode'
and c1.areaLabel = #{areaLabel}
GROUP BY c1.value order by c1.order_flag

SELECT ifnull(ROUND(SUM(c.tce) * 100 / s.target_value,2),0) speedValue ,ifnull(c.entName,'') name ,ifnull(ROUND(SUM(c.tce) ,2),0) energySum ,ifnull(s.target_value,0) targetEnergy ,c.company_id companyId FROM t_ent_energy_consume s, t_ent_energyinfo_comp_sum c WHERE s.company_id = c.company_id and year(c.date) = s.date and year(c.date) = #{date}
and s.company_id = #{companyId}
GROUP BY c.company_id ORDER BY energySum desc

select ifnull(ROUND(sum(feng),2),0) feng
	   ,ifnull(ROUND(sum(jian),2),0) jian
	   ,ifnull(ROUND(sum(gu),2),0) gu
 from t_ent_energyinfo_day
 where company_id = #{companyId}
  and year(date) =#{date}

SELECT ifnull( ROUND( sum(c.`value`) / sum(s.`value`) , 2 ), 0 ) AS unitPrice ,i.industryType ,i.company_id companyId FROM t_enterpriseinfo i LEFT JOIN (SELECT sum(c.`value`) value , company_id companyId FROM t_ent_dianfei_info c WHERE YEAR(c.date) = #{date} GROUP BY c.company_id ) c on i.company_id = c.companyId LEFT JOIN (SELECT sum(s.`value`) value ,company_id companyId FROM t_ent_energyinfo_comp_sum s WHERE YEAR(s.date) = #{date} and energyType = '0' GROUP BY s.company_id ) s on i.company_id = s.companyId WHERE 1=1
and i.industryType = #{industryType}
and i.company_id = #{companyId}
group by i.company_id
order by unitPrice asc
order by unitPrice desc

	SELECT r.contractcapacity
	  FROM t_ent_accnumber_rel r
	  WHERE r.company_id = #{companyId}
 	    and accNumber = #{accNumber}

SELECT date ,value
  FROM   t_ent_fuhe_info
 WHERE company_id = #{companyId}
 	  and accNumber = #{accNumber}
   AND year(date) = #{date}

SELECT ROUND( sum(value) ,2) value
		,month
  FROM (
		SELECT  SUM(`value`) / (count(accNumber) * 24) value
					 ,date month
		 FROM t_ent_energyinfo_day
		WHERE company_id = #{companyId}
		and YEAR(date) = #{date}
		and accnumber = #{accNumber}
		GROUP BY MONTH(date) ,accNumber
	   )p
	GROUP BY month

select date,ROUND(sum(value),2) as value from ( SELECT date , SUM(`value`)*0.0001229 value from t_ent_energyinfo_day_area e where DATE_FORMAT(date,'%Y-%m') = #{date}
and areaCode = #{areaCode}
GROUP BY date union all select date ,SUM(`value`)* 0.0341 value FROM t_ent_yongre_day where DATE_FORMAT(date,'%Y-%m') = #{date}
and areaCode = #{areaCode}
GROUP BY date ) s WHERE s.date != '' and s.date is not null group by date

SELECT aa.value , aa.monthValue , aa.yearValue , IFNULL((aa.value/aa.monthValue -1) * 100, '') monthRate , IFNULL((aa.value/aa.yearValue -1) * 100, '') yearRate from ( SELECT ROUND(SUM(d.tce),3) value, (SELECT ROUND(SUM(d.tce),3) value from t_enterpriseinfo e , t_ent_energyinfo_comp_sum d
INNER JOIN t_ent_tag_rel r ON r.identifier = d.company_id and r.tag_key = #{type}
where e.company_id = d.company_id
and e.areaCode = #{areaCode}
and DATE_FORMAT(d.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(STR_TO_DATE(CONCAT(#{date},'-01'),'%Y-%m-%d') ,INTERVAL 1 MONTH),'%Y-%m') GROUP BY DATE_FORMAT(d.date,'%Y-%m')) monthValue, (SELECT ROUND(SUM(d.tce),3) value from t_enterpriseinfo e , t_ent_energyinfo_comp_sum d
INNER JOIN t_ent_tag_rel r ON r.identifier = d.company_id and r.tag_key = #{type}
where e.company_id = d.company_id
and e.areaCode = #{areaCode}
and DATE_FORMAT(d.date,'%Y-%m') = DATE_FORMAT(DATE_SUB(STR_TO_DATE(CONCAT(#{date},'-01'),'%Y-%m-%d') ,INTERVAL 12 MONTH),'%Y-%m') GROUP BY DATE_FORMAT(d.date,'%Y-%m')) yearValue from t_ent_energyinfo_comp_sum d, t_enterpriseinfo e
INNER JOIN t_ent_tag_rel r ON r.identifier = e.company_id and r.tag_key = #{type}
where e.company_id = d.company_id
and e.areaCode = #{areaCode}
and DATE_FORMAT(d.date,'%Y-%m') = #{date} GROUP BY DATE_FORMAT(d.date,'%Y-%m')) aa

SELECT ROUND( sum(gu) * 100 / sum(value) ,2) as proportionValue ,i.industryType ,i.company_id companyId FROM t_enterpriseinfo i left join t_ent_energyinfo_day d on i.company_id = d.company_id and YEAR(d.date) = #{date} WHERE 1 = 1
and i.industryType = #{industryType}
and i.company_id = #{companyId}
group by i.company_id
order by proportionValue desc
order by proportionValue asc

select ifnull(ROUND( tce / gdpValue,2),0) as tceGDP ,ifnull(ROUND(tce,2),0) tce ,companyId ,industryType from ( SELECT sum(s.tce) tce ,e.company_id companyId ,e.industryType ,(select sum(value) from t_ent_economicsinfo where company_id = e.company_id and YEAR(date) = #{date} GROUP BY company_id) gdpValue FROM t_enterpriseinfo e LEFT JOIN t_ent_energyinfo_comp_sum s on e.company_id = s.company_id and YEAR(s.date) = #{date} WHERE 1=1
and e.industryType = #{industryType}
and e.company_id = #{companyId}
GROUP BY e.company_id
ORDER BY tce desc
ORDER BY tce asc
) p

SELECT e.date, TRUNCATE(SUM(e.value), 2) value
from t_ent_yongre_day e
where e.company_id = #{companyId} and e.accnumber =#{consNo}

    and DATE_FORMAT(e.date,'%Y-%m') = #{date}


    and PERIOD_DIFF(date_format(STR_TO_DATE(#{date}, '%Y-%m'),'%Y%m'),date_format(e.date,'%Y%m')) = 12

GROUP BY e.date ORDER BY e.date
